  select(fiscal_year, last_name, mid_init, first_name, agency_name, title_description, total_pay) |&gt;
  group_by(agency_name) |&gt;
  summarize(n = n())

remaining_agency_count &lt;- max(nrow(exceeding_agencies) - N_ITEMS, 0)

